const queryText=document.getElementById("query-text");function displayResults(e,t){const s=document.getElementById("results");if(e.length){queryText&&(queryText.innerText=`You searched for "${query}"`);let o="";for(const s in e){const n=t[e[s].ref];o+='<li><div><a href="'+n.url+'"><h5>'+n.title+"</h5></a>",o+='<p style="color: #a7a0b3; line-height: 1.2; margin: 0.2em 0 0; margin-bottom: 5px;">'+n.keywords+"</p>",o+="<span>"+n.authors+'</span><small style="white-space: nowrap;"><em> &mdash; '+n.publication+"</em></small></div></li>"}s.innerHTML=o}else queryText.innerText="",s.innerHTML="<li>No results found.</li>"}const params=new URLSearchParams(window.location.search),query=params.get("query");if(query){document.getElementById("search-input").setAttribute("value",query);displayResults(lunr((function(){this.ref("id"),this.field("title",{boost:15}),this.field("keywords"),this.field("authors"),this.field("content",{boost:10});for(const e in window.store)this.add({id:e,title:window.store[e].title,authors:window.store[e].authors,keywords:window.store[e].keywords,content:window.store[e].content})})).search(query),window.store)}